class Contain_detail extends CI_Controller{
    function __construct()
    {
        parent::__construct();
        $this->load->model('Contain_detail_model');
    } 

    /*
     * Listing of contain_details
     */
    function index()
    {
        $data['contain_details'] = $this->Contain_detail_model->get_all_contain_details();
        
        $data['_view'] = 'contain_detail/index';
        $this->load->view('layouts/main',$data);
    }

    /*
     * Adding a new contain_detail
     */
    function add()
    {   
        $this->load->library('form_validation');

		$this->form_validation->set_rules('section_title','Section Title','required');
		$this->form_validation->set_rules('contains','Contains','required');
		
		if($this->form_validation->run())     
        {   
            $params = array(
				'page_name' => $this->input->post('page_name'),
				'section_title' => $this->input->post('section_title'),
				'contains' => $this->input->post('contains'),
            );
            
            $contain_detail_id = $this->Contain_detail_model->add_contain_detail($params);
            redirect('contain_detail/index');
        }
        else
        {            
            $data['_view'] = 'contain_detail/add';
            $this->load->view('layouts/main',$data);
        }
    }  

    /*
     * Editing a contain_detail
     */
    function edit($conatin_details_id)
    {   
        // check if the contain_detail exists before trying to edit it
        $data['contain_detail'] = $this->Contain_detail_model->get_contain_detail($conatin_details_id);
        
        if(isset($data['contain_detail']['conatin_details_id']))
        {
            $this->load->library('form_validation');

			$this->form_validation->set_rules('section_title','Section Title','required');
			$this->form_validation->set_rules('contains','Contains','required');
		
			if($this->form_validation->run())     
            {   
                $params = array(
					'page_name' => $this->input->post('page_name'),
					'section_title' => $this->input->post('section_title'),
					'contains' => $this->input->post('contains'),
                );

                $this->Contain_detail_model->update_contain_detail($conatin_details_id,$params);            
                redirect('contain_detail/index');
            }
            else
            {
                $data['_view'] = 'contain_detail/edit';
                $this->load->view('layouts/main',$data);
            }
        }
        else
            show_error('The contain_detail you are trying to edit does not exist.');
    } 

    /*
     * Deleting contain_detail
     */
    function remove($conatin_details_id)
    {
        $contain_detail = $this->Contain_detail_model->get_contain_detail($conatin_details_id);

        // check if the contain_detail exists before trying to delete it
        if(isset($contain_detail['conatin_details_id']))
        {
            $this->Contain_detail_model->delete_contain_detail($conatin_details_id);
            redirect('contain_detail/index');
        }
        else
            show_error('The contain_detail you are trying to delete does not exist.');
    }
    
}
