class User_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get user by user_id
     */
    function checkLogin($uname,$pwd){
        $this->db->select('*');
        $this->db->from('users');
        $this->db->where('username',$uname);
        $this->db->where('password',$pwd);
        $query = $this->db->get();
        if($query->num_rows()>0){
            
            $data = $query->row();
            $this->session->set_userdata(array(
                'name'=>$data->fname.' '.$data->lname,
                'email'=>$data->email,
                'user_id'=>$data->user_id,
                'is_login'=>true,
                'student'=>$data->student,
                'promote'=>$data->promote,
                'class_section'=>$data->class_section,
                'teacher'=>$data->teacher,
                'payment'=>$data->payment,
                'fees'=>$data->fees,
                'leaving_certificate'=>$data->leaving_certificate,
                'website_contain'=>$data->website_contain,
                'reports'=>$data->reports,
                'user'=>$data->user,
            ));
            return true;
        }else{
            return false;
        }
        
    }
    function get_user($user_id)
    {
        return $this->db->get_where('users',array('user_id'=>$user_id))->row_array();
    }
        
    /*
     * Get all users
     */
    function get_all_users()
    {
        $this->db->order_by('user_id', 'desc');
        return $this->db->get('users')->result_array();
    }
        
    /*
     * function to add new user
     */
    function add_user($params)
    {
        $this->db->insert('users',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update user
     */
    function update_user($user_id,$params)
    {
        $this->db->where('user_id',$user_id);
        return $this->db->update('users',$params);
    }
    
    /*
     * function to delete user
     */
    function delete_user($user_id)
    {
        return $this->db->delete('users',array('user_id'=>$user_id));
    }
    
    function matchPassword($user_id,$password){
        $pass = sha1($password);
        $this->db->select('user_id');
        $this->db->from('users');
        $this->db->where('user_id',$user_id);
        $this->db->where('password',$pass);
        $query = $this->db->get();
        if($query->num_rows()>0){
            return true;
        }else{
            return false;
        }
    }
    function reset_password($user_id,$password){
        $this->db->where('user_id',$user_id);
        $this->db->set('password',sha1($password));
        if($this->db->update('users')){
            return true;
        }else{
            return false;
        }
    }
    
}
